import React, { useState } from 'react';
import './SmartContent.css';

interface User {
    name: string;
    mobileNumber: string;
    dataBalance: number;
}

const mockData: User[] = [
    { name: 'John Doe', mobileNumber: '09171234567', dataBalance: 1024 },
    { name: 'Jane Smith', mobileNumber: '09179876543', dataBalance: 512 },
    { name: 'Alice Johnson', mobileNumber: '09171239876', dataBalance: 256 },
];

const SmartContent: React.FC = () => {
    const [users, setUsers] = useState<User[]>(mockData);
    const [searchQuery, setSearchQuery] = useState<string>('');

    const filteredUsers = users.filter(user =>
        user.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        user.mobileNumber.includes(searchQuery)
    );

    const handleDelete = (index: number) => {
        const newUsers = [...users];
        newUsers.splice(index, 1);
        setUsers(newUsers);
    };

    const handleEdit = (index: number) => {
        // Implement edit functionality here
        alert(`Edit user: ${users[index].name}`);
    };

    const handleAddUser = () => {
        // Implement add user functionality here
        alert('Add new user');
    };

    return (
        <div className="smart-content">
            <div className="header">
                <div className="actions">
                    <input
                        type="text"
                        placeholder="Search..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="search-input"
                    />
                    <button className="add-user-button" onClick={handleAddUser}>Add User</button>
                </div>
            </div>
            <div className="user-cards">
                {filteredUsers.map((user, index) => (
                    <div className="user-card" key={index}>
                        <h4>{user.name}</h4>
                        <p><strong>Mobile Number:</strong> {user.mobileNumber}</p>
                        <p><strong>Remaining Data:</strong> {user.dataBalance} MB</p>
                        <div className="user-actions">
                            <button onClick={() => handleEdit(index)}>Edit</button>
                            <button onClick={() => handleDelete(index)}>Delete</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default SmartContent;
