import React, { useState, useEffect } from 'react';
import SmartContent from './SmartContent';
import './MonitoringDashboard.css';

interface MonitoringDashboardProps {
    selectedProvider: string;
}

const MonitoringDashboard: React.FC<MonitoringDashboardProps> = ({ selectedProvider }) => {
    const [dataBalance, setDataBalance] = useState<number | null>(null);

    useEffect(() => {
        // Mock API call to fetch data balance
        const fetchDataBalance = async () => {
            // Replace with actual API call
            const balance = await new Promise<number>((resolve) => setTimeout(() => resolve(1024), 1000));
            setDataBalance(balance);
        };

        fetchDataBalance();
    }, []);

    return (
        <div className="dashboard">
            <header className="dashboard-header">
                <h2>Monitoring Dashboard</h2>
            </header>
            <main className="dashboard-main">
                {selectedProvider === 'SMART' && <SmartContent />}
                {/* Add similar conditional rendering for DITO and GLOBE */}
                <section className="dashboard-section">
                    <h3>Data Balance</h3>
                    {dataBalance !== null ? (
                        <p>Remaining Data Balance: {dataBalance} MB</p>
                    ) : (
                        <p>Loading...</p>
                    )}
                </section>
                <section className="dashboard-section">
                    <h3>Usage History</h3>
                    <p>Placeholder for usage history</p>
                </section>
                <section className="dashboard-section">
                    <h3>Alerts</h3>
                    <p>Placeholder for alerts</p>
                </section>
            </main>
        </div>
    );
};

export default MonitoringDashboard;
